
public class switch_return {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String a = "Monday";
		
		switch(a){
		
		case "monday": System.out.print("Monday");
			break;
			
		case "Tuesday":System.out.print("Tuesay");
	
		default : System.out.print("None");	
		
			
		}

	}

}
